
find_floor :: String -> Int
find_floor xs = length (filter (=='(') xs) - length (filter (==')') xs) 


parse_to_array :: String -> [Int]
parse_to_array = map (\x -> if x == '(' then 1 else -1)


find_basement :: [Int] -> [Int] -> Int
find_basement (x:xs) path | (sum path) >= 0 = find_basement xs (x:path)
                          | otherwise = length (reverse path)
                          

find_basement' xs = length (takeWhile (>=0) (reverse (foldl (\acc x -> ((head acc) + x):acc) [0] xs)))
                          

problem_one = find_floor the_string

problem_two = find_basement the_numbers []

problem_two' = find_basement' the_numbers

the_numbers = parse_to_array the_string




































